package buclesejemplosenclase;

public class MiBucleFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Primero se hace la inicializacion 1 vez
		// Luego se ejecutan las intrucciones que hay dentro
		// Despues se incrementa
		// Por ultimo se evalua la condicion
		
		for(int i = 1; i <= 10; i++) {
			System.out.println(i);
		}
	}

}
